#region Functions
Function Install_vCACIS {

	# Load OVF/OVA configuration into a variable
	if ($vCACISOVA -eq "") {$vCACISOVA = Get-ChildItem -Path "C:\Temp" -filter *.ova | Where-Object { $_.length -gt 600mb -and $_.length -lt 800mb } ; $vCACISOVA = $vCACISOVA.FullName }
	$ISovfconfig = Get-OvfConfiguration $vCACISOVA

    Write-Host "vCACISOVA": $vCACISOVA
	# Get the values to use for deployment
	if ($VMHost -eq "") { $VMHost = Get-Cluster $cluster | Get-VMHost | Where { $_.PowerState -eq "PoweredOn" -and $_.ConnectionState -eq "Connected" } | Get-Random }
	if ($Datastore -eq "") { $Datastore = $VMhost | Get-Datastore | Where { $_.FreeSpaceGB -ge 5 } | Get-Random }

	$Network = Get-VirtualPortGroup -Name "$NetworkName" -VMHost $vmhost

	$ISovfconfig = @{
		"va-ssh-enabled" = "True"
		"varoot-password" = "$VCACISGP"
		"vami.netmask0.VMware_Identity_Appliance" = "$Netmask"
		"vami.gateway.VMware_Identity_Appliance" = "$Gateway"
		"vami.hostname" = "$VCACISFQDN"
		"vami.ip0.VMware_Identity_Appliance" = "$VCACISIP"
		"vami.DNS.VMware_Identity_Appliance" = "$DNS"
		"NetworkMapping.Network 1" = "$Network"
		"IpAssignment.IpProtocol" = "IPv4"
	}


# Deploy the OVF/OVA with the config parameters
	Import-VApp -Source $vCACISOVA -OvfConfiguration $ISovfconfig -Name $VCACISVM -VMHost $vmhost -Datastore $datastore -DiskStorageFormat Thin -Location $cluster
	Start-VM -VM $VCACISVM -confirm:$false -RunAsync
}
Function Install_vCACAP {

	# Load OVF/OVA configuration into a variable
	if ($vCACAPOVA -eq "" ) { $vCACAPOVA = Get-ChildItem -Path "c:\temp" -filter *.ova | Where-Object { $_.length -gt 1900mb -and $_.length -lt 2400mb } ; $vCACAPOVA = $vCACAPOVA.FullName }
	$APovfconfig = Get-OvfConfiguration $vCACAPOVA

	# Get the values to use for deployment
	if ($VMHost -eq "") { $VMHost = Get-Cluster $cluster | Get-VMHost | Where { $_.PowerState -eq "PoweredOn" -and $_.ConnectionState -eq "Connected" } | Get-Random }
	if ($Datastore -eq "") { $Datastore = $VMhost | Get-Datastore | Where { $_.FreeSpaceGB -ge 5 } | Get-Random }
	$Network = Get-VirtualPortGroup -Name "$NetworkName" -VMHost $vmhost

	# Fill out the OVF/OVA configuration parameters
	$APovfconfig = @{
		"va-ssh-enabled" = "True"
		"vami.netmask0.VMware_vRealize_Appliance" = "$Netmask"
		"varoot-password" = "$VCACAPGP"
		"vami.gateway.VMware_vRealize_Appliance" = "$Gateway"
		"vami.ip0.VMware_vRealize_Appliance" = "$VCACAPIP"
		"vami.hostname" = "$VCACAPFQDN"
		"vami.DNS.VMware_vRealize_Appliance" = "$DNS"
		"NetworkMapping.Network 1" = "$Network"
		"IpAssignment.IpProtocol" = "IPv4"
	}

	# Deploy the OVF/OVA with the config parameters
	Import-VApp -Source $vCACAPOVA -OvfConfiguration $APovfconfig -Name $VCACAPVM -VMHost $vmhost -Datastore $datastore -DiskStorageFormat Thin -Location $cluster
	Start-VM -VM $VCACAPVM -confirm:$false -RunAsync
}
Function Configure_IAAS {
