	Write-Host "Copying necessary files from the vCAC Appliance to local machine" -ForegroundColor Green

	#Decoding Files

	$Content = [System.Convert]::FromBase64String($MBED_AUTOMATIONSCRIPT)
	Set-Content -Path c:\temp\AutomationScript62.ps1 -Value $Content -Encoding Byte

	$Content = [System.Convert]::FromBase64String($MBED_SQLCHECK)
