	Copy-VMGuestFile -Source "C:\Temp\vCAC-Wapi-Setup.exe" -Destination "C:\Temp\vCAC-Wapi-Setup.exe" -VM $VCACIAASVM -LocaltoGuest -GuestUser $VCACIAASVMGU -GuestPassword $VCACIAASVMGP -force
	Write-Host "Done! Proceeding..." -ForegroundColor Green

	Write-Host "Copying WAPI-ConfigTool.xml to IAAS Machine" -ForegroundColor Yellow
	Copy-VMGuestFile -Source "C:\Temp\WAPI-ConfigTool.xml" -Destination "C:\Temp\WAPI-ConfigTool.xml" -VM $VCACIAASVM -LocaltoGuest -GuestUser $VCACIAASVMGU -GuestPassword $VCACIAASVMGP -force
	Write-Host "Done! Proceeding..." -ForegroundColor Green

	Write-Host "Copying vCAC-ServerConfig.xml to IAAS Machine" -ForegroundColor Yellow
	Copy-VMGuestFile -Source "C:\Temp\vCAC-ServerConfig.xml" -Destination "C:\Temp\vCAC-ServerConfig.xml" -VM $VCACIAASVM -LocaltoGuest -GuestUser $VCACIAASVMGU -GuestPassword $VCACIAASVMGP -force
	Write-Host "Done! Proceeding..." -ForegroundColor Green

	Write-Host "Copying WorkflowManagerInstaller.msi to IAAS Machine" -ForegroundColor Yellow
	Copy-VMGuestFile -Source "C:\Temp\WorkflowManagerInstaller.msi" -Destination "C:\Temp\WorkflowManagerInstaller.msi" -VM $VCACIAASVM -LocaltoGuest -GuestUser $VCACIAASVMGU -GuestPassword $VCACIAASVMGP -force
	Write-Host "Done! Proceeding..." -ForegroundColor Green

	Write-Host "Copying VrmAgentInstaller.msi to IAAS Machine" -ForegroundColor Yellow
	Copy-VMGuestFile -Source "C:\Temp\VrmAgentInstaller.msi" -Destination "C:\Temp\VrmAgentInstaller.msi" -VM $VCACIAASVM -LocaltoGuest -GuestUser $VCACIAASVMGU -GuestPassword $VCACIAASVMGP -force
	Write-Host "Done! Proceeding..." -ForegroundColor Green

	Write-Host "Copying websitecert script to IAAS" -ForegroundColor Yellow
	Copy-VMGuestFile -Source "C:\Temp\get-websitecert.ps1" -Destination "C:\Temp\get-websitecert.ps1" -VM $VCACIAASVM -LocaltoGuest -GuestUser $VCACIAASVMGU -GuestPassword $VCACIAASVMGP -force
	Write-Host "Done! Proceeding..." -ForegroundColor Green

	Write-Host "Copying Prereq automation.ps1 to IAAS Machine" -ForegroundColor Yellow
	Copy-VMGuestFile -Source "c:\Temp\AutomationScript62.ps1" -Destination "C:\Temp\AutomationScript62.ps1" -VM $VCACIAASVM -LocaltoGuest -GuestUser $VCACIAASVMGU -GuestPassword $VCACIAASVMGP -force
	Write-Host "Done! Proceeding..." -ForegroundColor Green

	Write-Host "Copying StartSQL.ps1 to IAAS Machine" -ForegroundColor Yellow
	Copy-VMGuestFile -Source "C:\Temp\StartSQL.ps1" -Destination "C:\Temp\StartSQL.ps1" -VM $VCACIAASVM -LocaltoGuest -GuestUser $VCACIAASVMGU -GuestPassword $VCACIAASVMGP -force
	Write-Host "Done! Proceeding..." -ForegroundColor Green

	Write-Host "Copying SQL Express Config.ini to IAAS Machine" -ForegroundColor Yellow
	Copy-VMGuestFile -Source "C:\Temp\SQLConfiguration.ini" -Destination "C:\Temp\SQLConfiguration.ini" -VM $VCACIAASVM -LocaltoGuest -GuestUser $VCACIAASVMGU -GuestPassword $VCACIAASVMGP -force
	Write-Host "Done! Proceeding..." -ForegroundColor Green

	Write-Host "Copying SQL Express to IAAS Machine" -ForegroundColor Yellow
	Copy-VMGuestFile -Source "C:\Temp\SQLEXPR_x64_ENU.exe" -Destination "C:\Temp\SQLEXPR_x64_ENU.exe" -VM $VCACIAASVM -LocaltoGuest -GuestUser $VCACIAASVMGU -GuestPassword $VCACIAASVMGP -force
	Write-Host "Done! Proceeding..." -ForegroundColor Green

	Write-Host "Copying SQL Service Checker to IAAS Machine" -ForegroundColor Yellow
	Copy-VMGuestFile -Source "C:\Temp\SQLCHECK.ps1" -Destination "C:\Temp\SQLCHECK.ps1" -VM $VCACIAASVM -LocaltoGuest -GuestUser $VCACIAASVMGU -GuestPassword $VCACIAASVMGP -force
	Write-Host "Done! Proceeding..." -ForegroundColor Green

	Write-Host "Copying InstallScript.bat to IAAS Machine" -ForegroundColor Yellow
	Copy-VMGuestFile -Source "C:\Temp\InstallScript.bat" -Destination "C:\Temp\InstallScript.bat" -VM $VCACIAASVM -LocaltoGuest -GuestUser $VCACIAASVMGU -GuestPassword $VCACIAASVMGP -force
	Write-Host "Done! Proceeding..." -ForegroundColor Green

	# --- File Transfer Complete ---


	# --- Run Installers on IAAS ---

	$command = ("Powershell -command `"& {Set-ExecutionPolicy bypass}`"")
	$bytes = [System.Text.Encoding]::Unicode.GetBytes($command)
	$encodedCommand = [Convert]::ToBase64String($bytes)
	Invoke-VMscript -VM $VCACIAASVM -GU $VCACIAASVMGU -GP $VCACIAASVMGP -scripttext "Powershell.exe -encodedCommand $encodedCommand" -scripttype bat
	Write-Host "Execution Policy Set!" -ForegroundColor Green

	$time = Get-Date
	Write-Host $time.ToShortTimeString()
	Write-Host "Running Prereq Automation Script, Please be Patient..." -ForegroundColor Yellow
	$RunPreReq = "C:\Temp\AutomationScript62.ps1 $serviceuser "
	Invoke-VMscript -VM $VCACIAASVM -GU $VCACIAASVMGU -GP $VCACIAASVMGP -scripttext $RunPreReq -ScriptType PowerShell
	Write-Host "vCAC IAAS Prereqs Done!" -ForegroundColor Green

	$time = Get-Date
	Write-Host $time.ToShortTimeString()
	Write-Host "Installing SQL, Please be Patient..." -ForegroundColor Yellow
	$RunInstall = 'Powershell.exe -File c:\temp\StartSQL.ps1 -verb RunAs'

	$command = ("c:\temp\SQLEXPR_x64_ENU.exe /iacceptSQLServerLicenseTerms /configurationfile=`"c:\temp\SQLConfiguration.ini`"")
	$bytes = [System.Text.Encoding]::Unicode.GetBytes($command)
	$encodedCommand = [Convert]::ToBase64String($bytes)

	#Invoke-VMscript -VM $VCACIAASVM -GU $VCACIAASVMGU -GP $VCACIAASVMGP -scripttext "Powershell.exe -encodedCommand $encodedCommand -wait " -scripttype bat
	Invoke-VMscript -VM $VCACIAASVM -GU $VCACIAASVMGU -GP $VCACIAASVMGP -scripttext $RunInstall -scripttype bat
	Write-Host "SQL Installation Done!" -ForegroundColor Green

	Write-Host "Waiting 60 seconds for SQL Express to Initialize" -ForegroundColor 'Yellow'
	Start-Sleep -Seconds 60
	Write-Host "Continuing" -ForegroundColor 'Yellow'

	Write-Host "Checking for SQL Services" -ForegroundColor 'Yellow'
	#$WaitforSQL = "Powershell.exe -File C:\Temp\SQLCHECK.ps1 -verb RunAs"
	$WaitforSQL = "C:\Temp\SQLCHECK.ps1 $VCACIAASVM "
	Invoke-VMscript -VM $VCACIAASVM -GU $VCACIAASVMGU -GP $VCACIAASVMGP -scripttext $WaitforSQL -ScriptType PowerShell
	Write-Host "SQL Services Running" -ForegroundColor 'Green'

	$time = Get-Date
	Write-Host $time.ToShortTimeString()
	Write-Host "Setting and Restarting Services" -ForegroundColor Yellow
	$autoSQLBrowser = "set-service -name SQLBrowser -startuptype Automatic; net start SQLBrowser; restart-Service 'MSSQL*SQLEXPRESS' -Force;restart-Service 'SQLWRITER' -Force"
	Invoke-VMscript -VM $VCACIAASVM -GU $VCACIAASVMGU -GP $VCACIAASVMGP -scripttext $autoSQLBrowser
	Write-Host "Service Configuration Complete!" -ForegroundColor Green

	# Attempting to set vCAC Appliance license key again
	Write-host "Adding vCAC License ..."
	$vCACLic = "/usr/sbin/vcac-config -v license-update --key $LicKey"
	Invoke-VMscript -VM $VCACAPVM -GU "root" -GP $VCACAPGP -scripttext $vCACLic
	Write-Host "License Key added"

	Write-Host "Getting vCAC Appliance Cert for IAAS"
	$script = "c:\Temp\get-websitecert.ps1 $vCACAPFQDN"
	Invoke-VMscript -VM $VCACIAASVM -GU $VCACIAASVMGU -GP $VCACIAASVMGP -scripttext $script
	Start-Sleep -Seconds 10

	$time = Get-Date
	Write-Host $time.ToShortTimeString()
	Write-Host "Running vCAC Installation, Please be Patient..." -ForegroundColor Yellow
	$RunInstall = { "C:\Temp\InstallScript.bat" }
	Invoke-VMscript -VM $VCACIAASVM -GU $VCACIAASVMGU -GP $VCACIAASVMGP -scripttext $RunInstall -scripttype bat
	Write-Host "vCAC IAAS Installation Done!" -ForegroundColor Green
}
Function Configure_vCACIS {
	if (!(Get-VM -Name $VCACISVM -ErrorAction 'SilentlyContinue')) { Throw "Cannot find $vCACISVM... Make sure $vCACISVM has deployed and try again" }
	Wait-Tools -VM $VCACISVM
	Write-host "Waiting 60 Seconds to ensure complete VM startup"
	Start-Sleep -Seconds 60
	$VCAC_config_Script = @"
#!/bin/bash
# Original Script by William Lam
# www.virtuallyghetto.com
# Updated by Mike Foley for VCAC 6.1 VA
# Updated by Brian Graf for a vRA full-installer solution
# Twitter: @vBrianGraf
# Date: 12/17/14
#--------------------------------------------------------------------------

VCAC_SSO_PASSWORD=$VCACISGP
VCAC_SSO_HOSTNAME=$VCACISFQDN
TIMEZONE="US/Pacific"
#11-Sep-14,MAF,Differs from Williams script in that on the SSO app you need to supply the list of NTP
#servers as two separated by a space and quoted values
NTP_SERVER01="$NTP_SERVER1"
NTP_SERVER02="$NTP_SERVER2"

SSL_CERT_ORGANIZATION=$CERT_ORG
SSL_CERT_ORGANIZATION_UNIT=$CERT_ORG_UNIT
SSL_CERT_COUNTRY=$CERT_COUNTRY
SSL_CERT_STATE=$CERT_STATE
SSl_CERT_CITY=$CERT_CITY
SSL_CERT_EMAIL=$CERT_EMAIL
SSL_STORE_FILE="/usr/lib/vmware-sts/conf/ssoserver.p12"

### DO NOT EDIT BEYOND HERE ###
VCAC_CONFIG_LOG=vCAC-IS.log
PRIVATE_KEY_FILE=server.key
CSR_FILE=server.csr
CERT_FILE=server.crt
PFX_FILE=ssoserver.p12

########## CONFIGURE ############
echo "Generating Private Key ..."
# OpenSSL Function in config-page.py
/usr/bin/openssl genrsa -aes256 -passout 'pass:123456' -out "`${PRIVATE_KEY_FILE}" 2048 >> "`${VCAC_CONFIG_LOG}" 2>&1

echo "Generating CSR ..."
/usr/bin/openssl req -new -key "`${PRIVATE_KEY_FILE}" -out "`${CSR_FILE}" -passin 'pass:vmware123' -utf8 -subj "/C=`${SSL_CERT_COUNTRY}/ST=`${SSL_CERT_STATE}/L=`${SSl_CERT_CITY}/O=`${SSL_CERT_ORGANIZATION}/OU=`${SSL_CERT_ORGANIZATION_UNIT}/CN=`${VCAC_SSO_HOSTNAME}/emailAddress=`${SSL_CERT_EMAIL}" >> "`${VCAC_CONFIG_LOG}" 2>&1

echo "Removing passphrase from Private Key ..."
/bin/cp "`${PRIVATE_KEY_FILE}" "`${PRIVATE_KEY_FILE}.org" >> "`${VCAC_CONFIG_LOG}" 2>&1
/usr/bin/openssl rsa -in "`${PRIVATE_KEY_FILE}.org" -out "`${PRIVATE_KEY_FILE}" -passin 'pass:vmware123' >> "`${VCAC_CONFIG_LOG}" 2>&1

echo "Generate Self-Signed Certificate ..."
/usr/bin/openssl x509 -req -days 365 -in "`${CSR_FILE}" -signkey "`${PRIVATE_KEY_FILE}" -out "`${CERT_FILE}" >> "`${VCAC_CONFIG_LOG}" 2>&1

echo "Copy the certificate to the correct location"
cp "`${CERT_FILE}" "`${SSL_STORE_FILE}"

echo "Restart the Identity Server to pick up the new certificate"
/etc/init.d/vmware-stsd stop
sleep 15
/etc/init.d/vmware-stsd start

echo "Waiting 5 minutes for VCAC SSO services to startup ..."
sleep 300

VCAC_CONFIG_LOG=vcac-id.log

echo -e "\nConfiguring NTP Server(s) to `${NTP_SERVERS}  ..."
/opt/vmware/share/vami/custom-services/bin/vami ntp use-ntp "`${NTP_SERVER01}" "`${NTP_SERVER02}" >> "`${VCAC_CONFIG_LOG}" 2>&1

echo "Configuring Timezone to `${TIMEZONE} ..."
/opt/vmware/share/vami/vami_set_timezone_cmd "`${TIMEZONE}" >> "`${VCAC_CONFIG_LOG}" 2>&1

echo "Configuring SSO ..."
/usr/lib/vmware-identity-va-mgmt/firstboot/vmware-identity-va-firstboot.sh --domain vsphere.local --password "`${VCAC_SSO_PASSWORD}"

sleep 240

echo "`${VCAC_SSO_HOSTNAME}" > /etc/vmware-identity/hostname.txt

echo

"@

	#Clean up the config script
	$VCAC_APP_Script_Clean = $VCAC_config_Script -replace "`r`n", "`n"
	$VCAC_APP_Script_Clean | Out-File -FilePath "c:\Temp\vcac_config.sh" -Encoding UTF8 -Force

	# Copy script to Log Insight
	Write-Host "[CONFIGURE] Copying vcac_config.sh to $VCACISVM"
	Copy-VMGuestFile -Source "c:\Temp\vcac_config.sh" -Destination "/storage/core/vcac_config.sh" -LocalToGuest -VM "$VCACISVM" -GuestUser root -GuestPassword "$VCACISGP" -force
	Write-Host "[CONFIGURE] Copy Complete"
	# Run CHMOD
	$chmod = "chmod +x /storage/core/vcac_config.sh"
	Write-Host "[CONFIGURE] Running CHMOD on vcac_config.sh"
	Invoke-VMScript -vm "$VCACISVM" -GuestUser root -GuestPassword "$VCACISGP" -ScriptType Bash -ScriptText $chmod
	# Run Script
	$time = Get-Date
	Write-Host $time.ToShortTimeString()
	Write-Host "[CONFIGURE] Running vcac_config.sh (This will take approximately 12 Minutes)"
	Invoke-VMScript -VM "$VCACISVM" -GuestUser root -GuestPassword "$VCACISGP" -ScriptType Bash -ScriptText "/storage/core/vcac_config.sh"
	Write-Host "Script Completed on $VCACISVM" -ForegroundColor 'Green'
	Write-host ""
}
Function Configure_vCACAP {
	if (!(Get-VM -Name $VCACAPVM -ErrorAction 'SilentlyContinue')) { Throw "Cannot find $vCACAPVM... Make sure $vCACAPVM has deployed and try again" }
$VCACVA_config_Script = @"
#!/bin/bash
# Script to automate the configuration of VCAC VA
# Updated by Brian Graf for a full-installer solution
# Twitter: @vBrianGraf
# Date: 9/30/14
#--------------------------------------------------------------------------
VCAC_SSO_SERVER=$VCACISFQDN
VCAC_SSO_PASSWORD=$VCACISGP
VCAC_VA_HOSTNAME=$VCACAPFQDN
NTP_SERVERS="$NTP_SERVERS"
TIMEZONE="US/Pacific"
SSL_CERT_ORGANIZATION=$CERT_ORG
SSL_CERT_ORGANIZATION_UNIT=$CERT_ORG_UNIT
SSL_CERT_COUNTRY=$CERT_COUNTRY
SSL_CERT_STATE=$CERT_STATE
SSl_CERT_CITY=$CERT_CITY
SSL_CERT_EMAIL=$CERT_EMAIL
VCAC_LICENSE_KEY=$LicKey
VCAC_CONFIG_LOG=vCAC-AP.log

### DO NOT EDIT BEYOND HERE ###
#!/bin/bash

echo -e "\nConfiguring NTP Server(s) to `${NTP_SERVERS}  ..."
/usr/sbin/vcac-vami ntp use-ntp "`${NTP_SERVERS}" >> "`${VCAC_CONFIG_LOG}" 2>&1

echo "Configuring Timezone to `${TIMEZONE} ..."
/opt/vmware/share/vami/vami_set_timezone_cmd "`${TIMEZONE}"

echo "Updating CAFE Hostname setting ..."
/usr/sbin/vcac-vami host update "`${VCAC_VA_HOSTNAME}" >> "`${VCAC_CONFIG_LOG}" 2>&1

# restart the DB
sed -i -re 's/^#(listen_addresses=.\*.)/\1/' /var/vmware/vpostgres/current/pgdata/postgresql.conf
/etc/init.d/vpostgres restart
sleep 15

# Generate SSL Certificate
/usr/sbin/vcac-config certificate-generate \
--common-name $VCACAPFQDN \
--organization $CERT_ORG \
--organizational-unit $CERT_ORG_UNIT \
--country $CERT_COUNTRY
sleep 10

# Copy the SSL Certificate to Apache
/usr/sbin/vcac-vami certificate-export-to-apache

sleep 10
# Register vcac with SSO
/usr/sbin/vcac-config -e sso-update \
--vcac-host $VCACAPFQDN \
--sso-host "`${VCAC_SSO_SERVER}:7444" \
--tenant vsphere.local \
--user $SSOUSERNAME \
--password $SSOPASS

sleep 40

echo "Upgrading DB..."
/usr/sbin/vcac-vami db-upgrade --upgrade --all && echo "Upgrading DB finished OK."

# Create as a service
chkconfig --add vcac-server

# RabbitMQ
test -e /etc/init.d/rabbitmq-server && {
    echo "###### Check rabbitmq status"
    date >> /var/log/rabbitmq/startup_log
    tail -n 4 /var/log/rabbitmq/startup_log
    ps auxww | grep rabbitmq
    # rabbitMQ cannot be stopped if hostname has changed https://stackoverflow.com/questions/14659335/rabbitmq-server-fails-to-start-after-hostname-has-changed-since-first-starting
    /etc/init.d/rabbitmq-server status || { echo "###### Restart rabbitmq"; kill `ps U rabbitmq | grep rabbitmq |  awk '{ print $1 }'`; /etc/init.d/rabbitmq-server restart; }
    sleep 1s
    tail -n 4 /var/log/rabbitmq/startup_log
    /etc/init.d/rabbitmq-server status
}

# Restart the service
echo "###### Restarting vCAC Service"
/etc/init.d/vcac-server restart

# Restart the service
echo "###### Restarting vCO Service"
/etc/init.d/vco-server restart

echo "Waiting 10 minutes for VCAC services to startup ..."
sleep 600

echo "Adding vCAC License ..."
/usr/sbin/vcac-config -v license-update --key $LicKey

echo "##### Done"
exit 0

"@

	#Clean up the config script
	$VCACAP_APP_Script_Clean = $VCACVA_config_Script -replace "`r`n", "`n"
	$VCACAP_APP_Script_Clean | Out-File -FilePath "c:\Temp\vcacva_config.sh" -Encoding UTF8 -Force

	# Copy script to Log Insight
	Write-Host "[CONFIGURE] Copying vcacva_config.sh to $VCACAPVM"
	Copy-VMGuestFile -Source "c:\Temp\vcacva_config.sh" -Destination "/storage/core/vcacva_config.sh" -LocalToGuest -VM "$VCACAPVM" -GuestUser root -GuestPassword "$VCACAPGP" -force
	Write-Host "[CONFIGURE] Copy Complete"
	# Run CHMOD
	$chmod = "chmod +x /storage/core/vcacva_config.sh"
	Write-Host "[CONFIGURE] Running CHMOD on vcacva_config.sh"
	Invoke-VMScript -vm "$VCACAPVM" -GuestUser root -GuestPassword "$VCACAPGP" -ScriptType Bash -ScriptText $chmod
	# Run Script
	$time = Get-Date
	Write-Host $time.ToShortTimeString()
	Write-Host "[CONFIGURE] Running vcacva_config.sh (This will take approximately 10 Minutes for services to start up)"
	Invoke-VMScript -VM "$VCACAPVM" -GuestUser root -GuestPassword "$VCACAPGP" -ScriptType Bash -ScriptText "/storage/core/vcacva_config.sh"
	Write-Host "Script Completed... Continuing" -ForegroundColor 'Green'
	Write-Host ""
	}
#endregion

if($LicKey -eq ""){Throw "No License Key has been added. This script will not work correctly without it. Please enter it and re-run this script." }
# Add PowerCLI Snapin
if (!(get-pssnapin -name VMware.VimAutomation.Core -erroraction 'SilentlyContinue')){
	Write-Host "[INFO] Adding PowerCLI Snapin" -ForegroundColor 'Yellow'
	add-pssnapin VMware.VimAutomation.Core -ErrorAction 'SilentlyContinue'
	if (!(get-pssnapin -name VMware.VimAutomation.Core -erroraction 'SilentlyContinue')){
		Throw "[ERROR] PowerCLI Not installed, please install from Http://VMware.com/go/PowerCLI"
	}
	Else{
		Write-Host "[INFO] PowerCLI Snapin added" -ForegroundColor 'Green'
	}
}

# PowerCLI Verification
$PowerCLIVersion = (Get-PowerCLIVersion)
if($PowerCLIVersion.Major -gt "5" -or ($PowerCLIVersion.Major -eq "5" -and $PowerCLIVersion.Minor -eq "8")) {Write-Host "Running PowerCLI Version $($PowerCLIVersion.Major).$($PowerCLIVersion.Minor)" -ForegroundColor 'Green' } else { Throw "This will only work with PowerCLI 5.8 or greater, Please install PowerCLI 5.8 or greater and re-run this script" }

# DNS Verification
Write-Host "Below are the results of our DNS Query. If a line is missing or invalid, please fix within your DNS and re-run this script:" -ForegroundColor 'green'

# Checking DNS
$machines = @("$VCACISFQDN", "$VCACAPFQDN", "$VCACIAASFQDN")
Write-host "Forward DNS Lookup:" -ForegroundColor "green"
foreach ($record in $machines)
{
	$NameServerQueried = "$DNS"
	$FullAddressList = nslookup -type=a $Record $NameServerQueried | findstr "[0-9].[0-9].[0-9].[0-9]"
	$counter = 0
	foreach ($line in $FullAddressList)
	{
		$counter = $counter + 1
		$line = $line -replace "Address:  "
		if ($counter -ne 1)
		{
			write-host "$record = $line" -foregroundcolor 'yellow' #| %{ $_.Split(" ")[2] }
		}
	}
}

$machines = @("$VCACISIP", "$VCACAPIP", "$VCACIAASIP")
Write-host "Reverse DNS Lookup:" -ForegroundColor "green"
foreach ($record in $machines)
{
	$NameServerQueried = "$DNS"
	$FullAddressList = nslookup -type=a $Record $NameServerQueried #| findstr "[0-9].[0-9].[0-9].[0-9]"
	$counter = 0
	foreach ($line in $FullAddressList)
	{
		$counter = $counter + 1

		if ($counter -ne 1)
		{
			if ($line -like "*Name*")
			{
				$line = $line -replace "Name:    "
				write-host "$record = $line" -foregroundcolor 'yellow'
			}
		}
	}
}
do
{
	$Correct = Read-Host -Prompt "Is everything correct? Hit 'Y' for Yes or 'N' for No: "
}
Until ($Correct -eq "Y" -or $Correct -eq "N")
Switch ($Correct)
{
	"Y" {Write-Host "Fantastic! Continuing..." -ForegroundColor Green }
	"N" {Exit}
}

# Connect to vCenter
Write-Host ""
Write-Host "Connecting to vCenter" -ForegroundColor 'Yellow'
Connect-VIServer $vCenter -user $vcuser -Password $vcpass -WarningAction SilentlyContinue
Write-Host "Connected to vCenter" -ForegroundColor 'Green'

# Menu
Write-Host "What would you like to do?
1 - Deploy & Configure both Virtual Appliances, Install & Configure IAAS (Requires Windows VM)
2 - Configure both Virtual Appliances (Already Deployed OVAs), Install & Configure IAAS (Requires Windows VM)
3 - Just Install & Configure IAAS (Pre-reqs, SQL, and vCAC Software)
4 - Exit
" -ForegroundColor Green
do {
	$ScriptAction = read-Host -Prompt "Enter the Menu Number Here: "
}
Until ($ScriptAction -eq "1" -or $ScriptAction -eq "2" -or $ScriptAction -eq "3" -or $ScriptAction -eq "4")
Switch ($ScriptAction) {
	"1" {
		if (get-vm $VCACAPVM -erroraction 'SilentlyContinue') {throw "The $VCACAPVM already exists. Either change the VM name in this script or delete current vCACAP VM"}

		if (get-vm $VCACISVM -erroraction 'SilentlyContinue') {throw "The $VCACISVM already exists. Either change the VM name in this script or delete current vCACIS VM"}
		Write-Host "Preparing to deploy vCAC Identity Server..." -ForegroundColor 'Yellow'
		Install_vCACIS
		Write-Host "Completed Identity Server Configuration" -ForegroundColor 'Green'
		Write-Host ""

		Write-Host "Preparing to deploy vCAC Appliance..." -ForegroundColor 'Yellow'
		Install_vCACAP
		Write-Host "Completed Identity Server Configuration" -ForegroundColor 'Green'
		Write-Host ""

		Write-Host "Starting Identity Server Configuration, Please be patient..." -ForegroundColor 'Yellow'
		Configure_vCACIS
		Write-Host "Completed Identity Server Configuration" -ForegroundColor 'Green'
		Write-Host ""

		Write-Host "Starting vCAC Appliance Configuration, Please be patient..." -ForegroundColor 'Yellow'
		Configure_vCACAP
		Write-Host "Completed vCAC Appliance Configuration" -ForegroundColor 'Green'
		Write-Host ""

		Write-Host "Starting IAAS Installation and Configuration
Please be aware of the following:
* This requires a Windows Server 2008r2 VM with Internet Connection
* If no internet connection is found, refer to documentation on how to install
* This should be run from a machine other than the IAAS server itself
" -ForegroundColor 'Yellow'
		Configure_IAAS
	}
	"2" {
		if (!(get-vm $VCACAPVM)) {throw "The $VCACAPVM Does not exist. Either Deploy the VM manually or run option 1"}

		if (!(get-vm $VCACISVM)) {throw "The $VCACISVM Does not exist. Either Deploy the VM manually or run option 1"}

		Write-Host "Starting Identity Server Configuration, Please be patient..." -ForegroundColor 'Yellow'
		Configure_vCACIS
		Write-Host "Completed Identity Server Configuration" -ForegroundColor 'Green'
		Write-Host ""

		Write-Host "Starting vCAC Appliance Configuration, Please be patient..." -ForegroundColor 'Yellow'
		Configure_vCACAP
		Write-Host "Completed vCAC Appliance Configuration" -ForegroundColor 'Green'
		Write-Host ""

		Write-Host "Starting IAAS Installation and Configuration
		Please be aware of the following:
		* This requires a Windows Server 2008r2 VM with Internet Connection
		* If no internet connection is found, refer to documentation on how to install
		* This should be run from a machine other than the IAAS server itself
		" -ForegroundColor 'Yellow'
		Configure_IAAS
	}
	"3" {
		Write-Host "Starting IAAS Installation and Configuration
		Please be aware of the following:
		* This requires a Windows Server 2008r2 VM with Internet Connection
		* If no internet connection is found, refer to documentation on how to install
		* This should be run from a machine other than the IAAS server itself
		" -ForegroundColor 'Yellow'
		Configure_IAAS
	}
	"4" { Exit }
}

